import React, { Component } from "react";
import axios from "axios";

class Fibonacci extends Component {
  state = {
    seenIndexes: [],
    index: "",
    val:0,
  };

  componentDidMount() {
    // this.fetchValues();
    this.fetchIndexes();
  }

  async fetchIndexes() {
    const values = await axios.get("/api/get");
    this.setState({ values: values.data });
  }

  // async fetchIndexes() {
  //   const seenIndexes = await axios.get("/api/values/all");
  //   this.setState({
  //     seenIndexes: seenIndexes.data,
  //   });
  // }

  handleSubmit = async (event) => {
    event.preventDefault();
    var valu=this.fib(this.state.index);
    console.log(valu);
    await axios.post("/api/insert", {
      setNumber: this.state.index,
    });

    this.setState({ index: "",val:valu});
    //this.fetchValues();
     this.fetchIndexes();
  };

  renderSeenIndexes() {
    return this.state.seenIndexes.map(({ number }) => number).join(", ");
  }

  fib(ind) {
    if(ind<2) return 1;
    return this.fib(ind-1)+this.fib(ind-2);
  }

  renderValues() {
    const entries = [];

    for (let key in this.state.values) {
      entries.push(
        <div key={key}>
          For index {key} I calculated {this.state.values[key]}
        </div>
      );
    }

    return entries;
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <label>Enter your index:</label>
          <input
            value={this.state.index}
            onChange={(event) => this.setState({ index: event.target.value })}
          />
          <button>Submit</button>
        </form>

        <h3>Indexes I have seen:</h3>
        <div><span>{this.state.val}</span></div>

        <h3>Calculated Values:</h3>
        {this.renderValues()}
      </div>
    );
  }
}

export default Fibonacci;
