import logo from './logo.svg';
import React,{Component} from 'react';
import './App.css';
import axios from 'axios';
class App extends Component {
  constructor(props){
    super(props)

    // Set initial state
    this.state = {comp : 'Hi, There!'}

    // Binding this keyword
    this.handleClick = this.openFibo.bind(this)
  }

  openFibo(){

    // Changing state
    this.setState({comp : 'Welcome to the React world!'})
  }
  openHelp(){
    this.setState({comp:'help'})
  }

  render(){
    return (
      <div>
        <h2>Message :</h2>

<p>{this.state.comp}</p>



        <button onClick={this.openFibo}>
          Fibo
        </button>
        <button onClick={this.openHelp}>
          Help
        </button>
      </div>
    )
  }
}

export default App
